import { 
    GET_PROFILE,
     PROFILE_ERROR, 
     CLEAR_PROFILE,
     UPDATE_PROFILE,
     ACCOUNT_DELETED
    } from "../actions/types";

const initialState = {
    profile: null,
    profiles: [],
    loading: true,
    error: {}

}

export default function profileReducer(state = initialState, action) {
    const { type, payload } = action;

    switch(type) {
        case GET_PROFILE:
        case UPDATE_PROFILE:
            return {
                ...state,
                profile: payload,
                loading: false
            };

        case PROFILE_ERROR:
            return {
                ...state,
                error: payload,
                loading: false
            };

        case CLEAR_PROFILE:
            return {
                ...state,
                profile: null,
                repos: [],
                loading: false
            };

        case ACCOUNT_DELETED:
            return {
                ...state,
                profile: null,
                repos: [],
                loading: false
            };

        default:
            return state;
    }
}
