import { 
    GET_PROFILE,
     PROFILE_ERROR, 
     CLEAR_PROFILE,
     UPDATE_PROFILE,
     ACCOUNT_DELETED,
     GET_PROFILES
    } from "../actions/types";

const initialState = {
    profile: null,
    profiles: [],
    loading: true,
    error: {}

}

export default function profileReducer(state = initialState, action) {
    const { type, payload } = action;

    switch(type) {
        case GET_PROFILE:
        case UPDATE_PROFILE:
            return {
                ...state,
                profile: payload,
                loading: false
            };
